    tag: Tag,
    f: Tool.Handler<Types.ExtractTag<Tools, Tag>, RH>
  ) => Handlers<Types.ExcludeTag<Tools, Tag>, R | RH | Tool.Context<Types.ExtractTag<Tools, Tag>>>
}

